import React from "react";
import crearPokemon from "../CrearContenedorPokemon";

export default function consultarPokemon(id:number) {
    fetch(`https://pokeapi.co/api/v2/pokemon/${id}`)
        .then(function (response) {
            response.json()
            .then(function (pokemon:{}[]) {
               crearPokemon(pokemon)

            })

        })
        // bien .then()
        // mal .catch()
}
