import React from "react";
import TrasfPokedex from "../componentes.tsx/DatosPokedex";
import Pokemondatos from "../CrearContenedorPokemon";

export default function consultarPokemon(id:number) {
    fetch(`https://pokeapi.co/api/v2/pokemon/${id}`)
        .then(function (response) {
            response.json()
            .then(function (pokemon) {
                Pokemondatos(pokemon)
                console.log(pokemon);




            })

        })
        // bien .then()
        // mal .catch()
}
